mapper.select:
SELECT COUNT(1)
FROM `cmdb_relgroup`
WHERE ci_id = #{ciId}
  AND name = #{name}

SELECT COUNT(1)
FROM cmdb_rel
WHERE from_name = #{fromName}
  AND to_name = #{toName}
  AND id != #{id}

SELECT COUNT(1)
FROM cmdb_rel
WHERE from_label = #{fromLabel}
  AND to_label = #{toLabel}
  AND id != #{id}
