mapper.select:
select a.id as id, rel_id AS relId, b.ci_id AS fromCiId, c.ci_id AS toCiId, from_cientity_id AS fromCiEntityId, to_cientity_id AS toCiEntityId, b.name AS fromCiEntityName, c.name AS toCiEntityName
,#{cacheFlushKey} AS cacheFlushKey
from cmdb_relentity a JOIN cmdb_cientity b ON a.from_cientity_id = b.id JOIN cmdb_cientity c ON a.to_cientity_id = c.id where a.expired_time <= NOW() LIMIT #{startNum}, #{pageSize}

SELECT distinct rel_id           AS relId,
                from_cientity_id AS fromCiEntityId
FROM cmdb_relentity a
         JOIN cmdb_cientity b ON a.from_cientity_id = b.id
WHERE rel_id = #{relId}
  AND b.ci_id = #{fromCiId}
LIMIT #{startNum} , #{pageSize}

SELECT distinct rel_id         AS relId,
                to_cientity_id AS toCiEntityId
FROM cmdb_relentity a
         JOIN cmdb_cientity c ON a.to_cientity_id = c.id
WHERE rel_id = #{relId}
  AND c.ci_id = #{toCiId}
LIMIT #{startNum} , #{pageSize}

SELECT a.id, rel_id AS relId, b.ci_id AS fromCiId, c.ci_id AS toCiId, from_cientity_id AS fromCiEntityId, to_cientity_id AS toCiEntityId, b.name AS fromCiEntityName, c.name AS toCiEntityName
,#{cacheFlushKey} AS cacheFlushKey
FROM cmdb_relentity a JOIN cmdb_cientity b ON a.from_cientity_id = b.id JOIN cmdb_cientity c ON a.to_cientity_id = c.id WHERE rel_id = #{relId} LIMIT #{startNum}, #{pageSize}

SELECT COUNT(1)
FROM `cmdb_relentity`
WHERE from_cientity_id = #{fromCiEntityId}
  AND to_cientity_id = #{toCiEntityId}
  AND rel_id = #{relId}

SELECT a.`to_cientity_id` FROM `cmdb_relentity` a WHERE a.from_cientity_id IN
#{item}
UNION SELECT a.`from_cientity_id` FROM `cmdb_relentity` a WHERE a.to_cientity_id IN
#{item}

SELECT a.`id`, a.`rel_id` AS relId, a.`from_cientity_id` AS fromCiEntityId, a.`to_cientity_id` AS toCiEntityId, a.`transaction_id` AS transactionId, b.`name` AS fromCiEntityName, c.`name` AS toCiEntityName, d.`from_ci_id` AS fromCiId, d.`to_ci_id` AS toCiId, d.`type_id` AS relTypeId, e.`name` AS relTypeName, 'from' AS direction FROM `cmdb_relentity` a JOIN `cmdb_cientity` b ON a.`from_cientity_id` = b.`id` JOIN `cmdb_cientity` c ON a.`to_cientity_id` = c.`id` JOIN `cmdb_rel` d ON a.rel_id = d.id JOIN `cmdb_reltype` e ON d.type_id = e.id WHERE a.from_cientity_id IN
#{item}
AND a.rel_id IN
#{item}
UNION SELECT a.`id`, a.`rel_id` AS relId, a.`from_cientity_id` AS fromCiEntityId, a.`to_cientity_id` AS toCiEntityId, a.`transaction_id` AS transactionId, b.`name` AS fromCiEntityName, c.`name` AS toCiEntityName, d.`from_ci_id` AS fromCiId, d.`to_ci_id` AS toCiId, d.`type_id` AS relTypeId, e.`name` AS relTypeName, 'to' AS direction FROM `cmdb_relentity` a JOIN `cmdb_cientity` b ON a.`from_cientity_id` = b.`id` JOIN `cmdb_cientity` c ON a.`to_cientity_id` = c.`id` JOIN `cmdb_rel` d ON a.rel_id = d.id JOIN `cmdb_reltype` e ON d.type_id = e.id WHERE a.to_cientity_id IN
#{item}
AND a.rel_id IN
#{item}

mapper.update:
update cmdb_relentity
set renew_time = now(3)
where id = #{value}

update cmdb_relentity
set from_index = null
where rel_id = #{relId}
  and from_cientity_id = #{ciEntityId}
  and from_index is not null

update cmdb_relentity
set to_index = null
where rel_id = #{relId}
  and to_cientity_id = #{ciEntityId}
  and to_index is not null

mapper.insert:
INSERT INTO `cmdb_relentity` (`id`, `rel_id`, `from_cientity_id`, `to_cientity_id`, `transaction_id`, `insert_time`, `renew_time`, `valid_day`, `source_relentity_id`, `relativerel_hash`
,`expired_time` ) VALUES ( #{id}, #{relId}, #{fromCiEntityId}, #{toCiEntityId}, #{transactionId}, now(3), now(3), #{validDay}, #{sourceRelEntityId}, #{relativeRelHash}
,DATE_ADD(NOW(3),INTERVAL #{validDay} DAY) )

mapper.delete:
DELETE
FROM `cmdb_relentity`
WHERE from_cientity_id = #{fromCiEntityId}
  AND rel_id = #{relId}

DELETE
FROM `cmdb_relentity`
WHERE to_cientity_id = #{toCiEntityId}
  AND rel_id = #{relId}

DELETE
FROM `cmdb_relentity`
WHERE rel_id = #{relId}
  AND from_cientity_id = #{fromCiEntityId}
  AND to_cientity_id = #{toCiEntityId}
