mapper.select:
SELECT COUNT(1)
FROM `cmdb_relentity`
WHERE from_cientity_id = #{fromCiEntityId}
  AND to_cientity_id = #{toCiEntityId}
  AND rel_id = #{relId}

SELECT a.`to_cientity_id` FROM `cmdb_relentity` a WHERE a.from_cientity_id IN
#{item}
UNION SELECT a.`from_cientity_id` FROM `cmdb_relentity` a WHERE a.to_cientity_id IN
#{item}

SELECT a.`id`, a.`rel_id` AS relId, a.`from_cientity_id` AS fromCiEntityId, a.`to_cientity_id` AS toCiEntityId, a.`transaction_id` AS transactionId, b.`name` AS fromCiEntityName, c.`name` AS toCiEntityName, d.`from_ci_id` AS fromCiId, d.`to_ci_id` AS toCiId, d.`type_id` AS relTypeId, e.`name` AS relTypeName, 'from' AS direction FROM `cmdb_relentity` a JOIN `cmdb_cientity` b ON a.`from_cientity_id` = b.`id` JOIN `cmdb_cientity` c ON a.`to_cientity_id` = c.`id` JOIN `cmdb_rel` d ON a.rel_id = d.id JOIN `cmdb_reltype` e ON d.type_id = e.id WHERE a.from_cientity_id IN
#{item}
AND a.rel_id IN
#{item}
UNION SELECT a.`id`, a.`rel_id` AS relId, a.`from_cientity_id` AS fromCiEntityId, a.`to_cientity_id` AS toCiEntityId, a.`transaction_id` AS transactionId, b.`name` AS fromCiEntityName, c.`name` AS toCiEntityName, d.`from_ci_id` AS fromCiId, d.`to_ci_id` AS toCiId, d.`type_id` AS relTypeId, e.`name` AS relTypeName, 'to' AS direction FROM `cmdb_relentity` a JOIN `cmdb_cientity` b ON a.`from_cientity_id` = b.`id` JOIN `cmdb_cientity` c ON a.`to_cientity_id` = c.`id` JOIN `cmdb_rel` d ON a.rel_id = d.id JOIN `cmdb_reltype` e ON d.type_id = e.id WHERE a.to_cientity_id IN
#{item}
AND a.rel_id IN
#{item}

mapper.delete:
DELETE
FROM `cmdb_relentity`
WHERE from_cientity_id = #{fromCiEntityId}
  AND rel_id = #{relId}

DELETE
FROM `cmdb_relentity`
WHERE to_cientity_id = #{toCiEntityId}
  AND rel_id = #{relId}

DELETE
FROM `cmdb_relentity`
WHERE rel_id = #{relId}
  AND from_cientity_id = #{fromCiEntityId}
  AND to_cientity_id = #{toCiEntityId}
