select cra.`id`, cra.`name`, cra.`account`, cra.`password` as passwordCipher, cra.`fcu`, cra.`fcd`, cra.`lcu`, cra.`lcd` from cmdb_resourcecenter_resource_account crra left join cmdb_resourcecenter_account cra on crra.account_id = cra.id LEFT JOIN cmdb_resourcecenter_account_protocol crap on cra.protocol_id = crap.id where crra.resource_id = #{resourceId} and crap.`name` = #{protocol}
and crap.`port` = #{protocolPort}
and cra.account = #{username}

