mapper.select:
SELECT customview_id as customViewId, name, template, config, is_active AS isActive
FROM cmdb_customview_template
WHERE customview_id = #{value}

SELECT count(1)
FROM `cmdb_customview_attr`
WHERE customview_id = #{customViewId}
  and name = #{name}

select a.customview_id AS customViewId, a.customview_ci_uuid AS customViewCiUuid, a.uuid, a.const_name AS constName, a.alias, a.name, a.sort, a.is_hidden AS isHidden, a.is_primary AS isPrimary from cmdb_customview_constattr a JOIN cmdb_customview_ci b ON a.customview_ci_uuid = b.uuid and b.is_hidden = 0 where a.customview_id = #{customViewId}
AND a.is_hidden = #{isHidden}
ORDER BY a.sort

select count(1)
from cmdb_customview
where name = #{name}
  and id != #{id}

SELECT a.customview_id AS customViewId, a.customview_ci_uuid AS customViewCiUuid, a.uuid, a.attr_id AS attrId, a.alias, a.sort, a.is_hidden AS isHidden, a.is_primary AS isPrimary, a.`condition` AS conditionStr, a.`name` AS name, b.`name` AS attrName, b.`label` AS attrLabel, b.`ci_id` AS ciId, b.`type` AS attrType, b.`target_ci_id` AS targetCiId, b.`config` AS configStr FROM `cmdb_customview_attr` a JOIN `cmdb_attr` b ON a.attr_id = b.id JOIN `cmdb_customview_ci` c ON a.customview_ci_uuid = c.uuid AND c.is_hidden = 0 WHERE a.customview_id = #{customViewId}
AND a.is_hidden = #{isHidden}
ORDER BY a.sort

SELECT a.customview_id AS customViewId, a.customview_ci_uuid AS customViewCiUuid, a.uuid, a.attr_id AS attrId, a.alias, a.sort, a.is_hidden AS isHidden, a.is_primary AS isPrimary, a.`condition` AS conditionStr, a.`name` AS name, b.`name` AS attrName, b.`label` AS attrLabel, b.`ci_id` AS ciId, b.`type` AS attrType, b.`target_ci_id` AS targetCiId, b.`config` AS configStr FROM `cmdb_customview_attr` a JOIN `cmdb_attr` b ON a.attr_id = b.id JOIN `cmdb_customview_ci` c ON a.customview_ci_uuid = c.uuid AND c.is_hidden = 0 WHERE a.uuid IN
#{uuid}
ORDER BY a.sort

choose.otherwise:
AND (
(cc.type = 'private' AND cc.fcu = #{fcu})
OR
(cca.`auth_type` = 'common' AND cca.`auth_uuid` = 'alluser')

    OR (cca.`auth_type` = 'user' AND cca.`auth_uuid` = #{userUuid})


    OR (cca.`auth_type` = 'team' AND cca.`auth_uuid` IN

        #{teamUuid}

    )


    OR (cca.`auth_type` = 'role' AND cca.`auth_uuid` IN

        #{roleUuid}

    )

)

mapper.update:
UPDATE cmdb_customview
SET is_active = #{isActive}
WHERE id = #{id}
