mapper.select:
select count(1)
from cmdb_global_attr
where id != #{id}
  and name = #{name}

select cga.id, cga.name, cga.label, cga.is_active AS isActive, cga.is_multiple AS isMultiple, cga.description As description, cgai.id as itemId, cgai.value as itemValue, cgai.attr_id as itemAttrId, cgai.sort as itemSort from cmdb_global_attr cga left join cmdb_global_attritem cgai on cga.id = cgai.attr_id
is_active = #{isActive}
order by cga.id desc, cgai.sort

mapper.update:
update cmdb_global_attritem
set value = #{value},
    sort  = #{sort}
where id = #{id}

mapper.delete:
delete
from cmdb_cientity_globalattritem
where cientity_id = #{ciEntityId}
  and attr_id = #{attrId}
