mapper.select:
SELECT `ci_id` FROM `cmdb_ci_group` a join cmdb_group b on a.group_id = b.id WHERE a.group_id IN
#{item}
AND a.ci_id IN
#{item}
AND b.type IN
#{item}

SELECT a.`cientity_id` FROM `cmdb_cientity_group` a join `cmdb_group` b on a.group_id = b.id WHERE a.group_id IN
#{item}
AND a.cientity_id IN
#{item}
AND b.type IN
#{item}

select count(1)
from cmdb_cientity_group
where group_id = #{value}

select a.id, a.is_active as isActive, a.name, a.type, a.description, a.cientity_count AS ciEntityCount, a.status AS status, a.error as error, b.id as ciGroupId, b.ci_id as ciId, b.rule as ruleStr, c.group_id as authGroupId, c.auth_type as authType, c.auth_uuid as authUuid from cmdb_group a left join cmdb_ci_group b on a.id = b.group_id join cmdb_group_auth c on a.id = c.group_id and ((c.auth_type = 'common' AND c.auth_uuid = 'alluser')
OR ( c.auth_type = 'user' AND c.auth_uuid = #{userUuid} )
OR ( c.auth_type = 'team' AND c.auth_uuid IN
#{item}
)
OR ( c.auth_type = 'role' AND c.auth_uuid IN
#{item} )
)

SELECT -1 AS group_id FROM `cmdb_group_auth` WHERE FALSE
UNION SELECT `group_id` FROM `cmdb_group_auth` WHERE auth_type = 'user' AND auth_uuid = #{userUuid}
UNION SELECT `group_id` FROM `cmdb_group_auth` WHERE auth_type = 'team' AND auth_uuid IN
#{item}
UNION SELECT `group_id` FROM `cmdb_group_auth` WHERE auth_type = 'role' AND auth_uuid IN
#{item}
UNION SELECT `group_id` FROM `cmdb_group_auth` WHERE auth_type = 'common' AND auth_uuid = 'alluser'

mapper.update:
update cmdb_group
set cientity_count = #{ciEntityCount}
where id = #{id}

update cmdb_group
set status    = #{status},
    server_id = #{serverId},
    error     = #{error}
where id = #{id}

mapper.delete:
delete
from cmdb_cientity_group
where cientity_id = #{ciEntityId}
  and ci_group_id = #{ciGroupId}
